import type { Express } from "express";
import { createServer, type Server } from "http";
import multer from "multer";
import mammoth from "mammoth";
import { storage } from "./storage";
import { OpenAIService } from "./services/openai";
import { TTSService } from "./services/tts";
import { podcastGenerationRequestSchema } from "@shared/schema";
import path from "path";
import fs from "fs";

const upload = multer({ dest: "uploads/" });
const openaiService = new OpenAIService();
const ttsService = new TTSService();

export async function registerRoutes(app: Express): Promise<Server> {
  // File upload endpoint for document processing
  app.post("/api/upload-document", upload.single("document"), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ error: "No file uploaded" });
      }

      let content: string;

      if (req.file.mimetype === "application/vnd.openxmlformats-officedocument.wordprocessingml.document") {
        // Handle .docx files
        const result = await mammoth.extractRawText({ path: req.file.path });
        content = result.value;
      } else if (req.file.mimetype === "text/plain") {
        // Handle .txt files
        content = fs.readFileSync(req.file.path, "utf-8");
      } else {
        return res.status(400).json({ error: "Unsupported file type. Please upload .docx or .txt files." });
      }

      // Clean up uploaded file
      fs.unlinkSync(req.file.path);

      if (content.length < 100) {
        return res.status(400).json({ error: "Document content is too short. Please provide at least 100 characters." });
      }

      res.json({ content });
    } catch (error) {
      console.error("Document upload error:", error);
      res.status(500).json({ error: "Failed to process document" });
    }
  });

  // Content analysis endpoint
  app.post("/api/analyze-content", async (req, res) => {
    try {
      const { content } = req.body;

      if (!content || typeof content !== "string" || content.length < 100) {
        return res.status(400).json({ error: "Content must be at least 100 characters long" });
      }

      const analysis = await openaiService.analyzeContent(content);
      res.json(analysis);
    } catch (error) {
      console.error("Content analysis error:", error);
      res.status(500).json({ error: "Failed to analyze content" });
    }
  });

  // Podcast generation endpoint
  app.post("/api/generate-podcast", async (req, res) => {
    try {
      const validatedData = podcastGenerationRequestSchema.parse(req.body);

      // Create podcast record
      const podcast = await storage.createPodcast({
        title: "Generated Podcast",
        originalContent: validatedData.content,
        generatedScript: { segments: [], estimatedDuration: 0 },
        audioUrl: null,
        duration: null,
        settings: validatedData,
        status: "processing"
      });

      // Start async generation process
      generatePodcastAsync(podcast.id, validatedData);

      res.json({ podcastId: podcast.id, status: "processing" });
    } catch (error) {
      console.error("Podcast generation error:", error);
      if (error instanceof Error && error.name === "ZodError") {
        return res.status(400).json({ error: "Invalid request data", details: error.message });
      }
      res.status(500).json({ error: "Failed to start podcast generation" });
    }
  });

  // Podcast status endpoint
  app.get("/api/podcast/:id/status", async (req, res) => {
    try {
      const podcast = await storage.getPodcast(req.params.id);
      if (!podcast) {
        return res.status(404).json({ error: "Podcast not found" });
      }

      res.json({
        id: podcast.id,
        status: podcast.status,
        title: podcast.title,
        generatedScript: podcast.generatedScript,
        audioUrl: podcast.audioUrl,
        duration: podcast.duration
      });
    } catch (error) {
      console.error("Status check error:", error);
      res.status(500).json({ error: "Failed to get podcast status" });
    }
  });

  // Audio serving endpoint
  app.get("/api/audio/:fileName", (req, res) => {
    try {
      const fileName = req.params.fileName;
      const filePath = ttsService.getAudioFilePath(fileName);

      if (!fs.existsSync(filePath)) {
        return res.status(404).json({ error: "Audio file not found" });
      }

      const stat = fs.statSync(filePath);
      res.writeHead(200, {
        "Content-Type": "audio/mpeg",
        "Content-Length": stat.size,
        "Cache-Control": "public, max-age=3600",
      });

      const readStream = fs.createReadStream(filePath);
      readStream.pipe(res);
    } catch (error) {
      console.error("Audio serving error:", error);
      res.status(500).json({ error: "Failed to serve audio file" });
    }
  });

  // Get recent podcasts
  app.get("/api/podcasts/recent", async (req, res) => {
    try {
      const podcasts = await storage.getRecentPodcasts();
      res.json(podcasts);
    } catch (error) {
      console.error("Recent podcasts error:", error);
      res.status(500).json({ error: "Failed to get recent podcasts" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

// Async function to handle podcast generation
async function generatePodcastAsync(podcastId: string, settings: any) {
  try {
    console.log(`🎙️ Starting podcast generation for ID: ${podcastId}`);
    console.log(`📝 Content length: ${settings.content.length} characters`);

    // Update status to script generation
    console.log("🔄 Updating status to generating_script");
    await storage.updatePodcastStatus(podcastId, "generating_script");

    // Generate script
    console.log("🤖 Calling OpenAI to generate script...");
    const script = await openaiService.generatePodcastScript(settings.content, settings);
    console.log(`✅ Script generated with ${script.segments.length} segments`);

    // Update with generated script
    console.log("🔄 Updating podcast with generated script");
    await storage.updatePodcast(podcastId, {
      generatedScript: script,
      status: "generating_audio"
    });

    // Generate audio
    console.log("🎵 Starting TTS audio generation...");
    const audioResult = await ttsService.synthesizePodcast(script, {
      maleVoice: settings.maleVoice,
      femaleVoice: settings.femaleVoice,
      maleSpeed: settings.maleSpeed,
      femaleSpeed: settings.femaleSpeed
    });
    console.log(`✅ Audio generated: ${audioResult.audioUrl}, duration: ${audioResult.duration}s`);

    // Update with completed audio
    console.log("🔄 Updating podcast status to completed");
    await storage.updatePodcast(podcastId, {
      audioUrl: audioResult.audioUrl,
      duration: Math.round(audioResult.duration),
      status: "completed"
    });

    console.log("🎉 Podcast generation completed successfully!");

  } catch (error) {
    console.error("❌ Async generation error:", error);
    console.error("📍 Error stack:", error instanceof Error ? error.stack : 'No stack trace');
    await storage.updatePodcastStatus(podcastId, "failed");
  }
}

server/storage.ts.content:
import { type User, type InsertUser, type Podcast, type InsertPodcast } from "@shared/schema";
import { randomUUID } from "crypto";

// modify the interface with any CRUD methods
// you might need

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Podcast methods
  createPodcast(podcast: InsertPodcast): Promise<Podcast>;
  getPodcast(id: string): Promise<Podcast | undefined>;
  getRecentPodcasts(): Promise<Podcast[]>;
  updatePodcast(id: string, updates: Partial<Podcast>): Promise<Podcast | undefined>;
  updatePodcastStatus(id: string, status: string): Promise<void>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private podcasts: Map<string, Podcast>;

  constructor() {
    this.users = new Map();
    this.podcasts = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  // Podcast methods implementation
  async createPodcast(insertPodcast: InsertPodcast): Promise<Podcast> {
    const id = randomUUID();
    const podcast: Podcast = { 
      ...insertPodcast,
      id,
      createdAt: new Date(),
      status: insertPodcast.status || "processing", // Ensure status is always defined
      audioUrl: insertPodcast.audioUrl || null, // Ensure audioUrl is string | null, not undefined
      duration: insertPodcast.duration || null // Ensure duration is number | null, not undefined
    };
    this.podcasts.set(id, podcast);
    return podcast;
  }

  async getPodcast(id: string): Promise<Podcast | undefined> {
    return this.podcasts.get(id);
  }

  async getRecentPodcasts(): Promise<Podcast[]> {
    const podcasts = Array.from(this.podcasts.values());
    // Sort by creation date, most recent first, and limit to 10
    return podcasts
      .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
      .slice(0, 10);
  }

  async updatePodcast(id: string, updates: Partial<Podcast>): Promise<Podcast | undefined> {
    const existing = this.podcasts.get(id);
    if (!existing) return undefined;

    const updated = { ...existing, ...updates };
    this.podcasts.set(id, updated);
    return updated;
  }

  async updatePodcastStatus(id: string, status: string): Promise<void> {
    const existing = this.podcasts.get(id);
    if (existing) {
      existing.status = status;
      this.podcasts.set(id, existing);
    }
  }
}

export const storage = new MemStorage();

