  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

.dark {
  --background: hsl(0 0% 0%);
  --foreground: hsl(200 6.6667% 91.1765%);
  --card: hsl(228 9.8039% 10%);
  --card-foreground: hsl(0 0% 85.0980%);
  --popover: hsl(0 0% 0%);
  --popover-foreground: hsl(200 6.6667% 91.1765%);
  --primary: hsl(221.2 83.2% 53.3%);
  --primary-foreground: hsl(210 40% 98%);
  --secondary: hsl(195.0000 15.3846% 94.9020%);
  --secondary-foreground: hsl(210 25% 7.8431%);
  --muted: hsl(0 0% 9.4118%);
  --muted-foreground: hsl(210 3.3898% 46.2745%);
  --accent: hsl(38 92% 50%);
  --accent-foreground: hsl(222.2 84% 4.9%);
  --destructive: hsl(0 84.2% 60.2%);
  --destructive-foreground: hsl(210 40% 98%);
  --border: hsl(210 5.2632% 14.9020%);
  --input: hsl(207.6923 27.6596% 18.4314%);
  --ring: hsl(221.2 83.2% 53.3%);
  --sidebar: hsl(228 9.8039% 10%);
  --sidebar-foreground: hsl(0 0% 85.0980%);
  --sidebar-primary: hsl(221.2 83.2% 53.3%);
  --sidebar-primary-foreground: hsl(210 40% 98%);
  --sidebar-accent: hsl(228 9.8039% 10%);
  --sidebar-accent-foreground: hsl(221.2 83.2% 53.3%);
  --sidebar-border: hsl(210 5.2632% 14.9020%);
  --sidebar-ring: hsl(221.2 83.2% 53.3%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Custom animations and effects */
.fade-in {
  animation: fadeIn 0.5s ease-in;
}

@keyframes fadeIn {
  from { 
    opacity: 0; 
    transform: translateY(20px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

.processing-spinner {
  animation: spin 2s linear infinite;
}

.progress-bar {
  background: linear-gradient(90deg, hsl(221.2 83.2% 53.3%), hsl(38 92% 50%));
  transition: width 0.3s ease;
}

/* Waveform animation */
.waveform {
  background: linear-gradient(90deg, hsl(221.2 83.2% 53.3%) 0%, hsl(38 92% 50%) 50%, hsl(221.2 83.2% 53.3%) 100%);
  height: 2px;
  width: 100%;
  margin: 1rem 0;
  border-radius: 1px;
}

/* Upload zone hover effects */
.upload-zone {
  transition: all 0.3s ease;
}

.upload-zone:hover {
  border-color: hsl(221.2 83.2% 53.3%);
  background-color: hsl(221.2 83.2% 98%);
}

.upload-zone.dragover {
  border-color: hsl(221.2 83.2% 53.3%);
  background-color: hsl(221.2 83.2% 95%);
  transform: scale(1.02);
}

