import OpenAI from "openai";
import type { PodcastGenerationRequest, GeneratedScript } from "@shared/schema";

// the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key"
});

export class OpenAIService {
  async generatePodcastScript(content: string, settings: PodcastGenerationRequest): Promise<GeneratedScript> {
    const lengthInstructions = {
      brief: "3-5 minutes (about 450-750 words)",
      standard: "5-8 minutes (about 750-1200 words)",
      detailed: "8-12 minutes (about 1200-1800 words)",
      indepth: "12-15 minutes (about 1800-2250 words)"
    };

    const toneInstructions = {
      professional: "professional and informative tone with authoritative delivery",
      conversational: "conversational and engaging tone with natural flow",
      casual: "casual and friendly tone with relaxed interaction",
      academic: "academic and analytical tone with detailed explanations"
    };

    const prompt = `You are an expert podcast script writer. Create an engaging dialogue between two podcast hosts discussing the following article content.

ARTICLE CONTENT:
${content}

REQUIREMENTS:
- Target length: ${lengthInstructions[settings.targetLength]}
- Discussion tone: ${toneInstructions[settings.tone]}
- Male host voice: ${settings.maleVoice}
- Female host voice: ${settings.femaleVoice}
- Include intro and outro: ${settings.includeIntro}

SCRIPT FORMAT:
- Create natural, engaging dialogue between the two hosts
- The male host should be knowledgeable and provide context
- The female host should ask insightful questions and provide analysis
- Include natural transitions, pauses, and conversational elements
- Make sure the discussion covers the key points from the article
- Keep the conversation flowing naturally without being repetitive

${settings.includeIntro ? 'Start with a brief intro where hosts introduce themselves and the topic.' : ''}
${settings.includeIntro ? 'End with a brief outro and closing remarks.' : ''}

Return the response as JSON in this exact format:
{
  "segments": [
    {
      "speaker": "male",
      "name": "${settings.maleVoice}",
      "content": "Welcome to our podcast..."
    },
    {
      "speaker": "female", 
      "name": "${settings.femaleVoice}",
      "content": "Thank you, and today we're discussing..."
    }
  ],
  "estimatedDuration": 360
}`;

    try {
      const response = await openai.chat.completions.create({
        model: "gpt-5",
        messages: [
          {
            role: "system",
            content: "You are an expert podcast script writer who creates engaging, natural conversations between two hosts. Always respond with valid JSON in the specified format."
          },
          {
            role: "user",
            content: prompt
          }
        ],
        response_format: { type: "json_object" },
        temperature: 1.0,
        max_completion_tokens: 4000,
      });

      const result = JSON.parse(response.choices[0].message.content || "{}");

      if (!result.segments || !Array.isArray(result.segments)) {
        throw new Error("Invalid script format returned from OpenAI");
      }

      return result as GeneratedScript;
    } catch (error) {
      console.error("Error generating podcast script:", error);
      throw new Error(`Failed to generate podcast script: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  async analyzeContent(content: string): Promise<{ title: string; summary: string; keyPoints: string[] }> {
    const prompt = `Analyze the following content and provide a title, summary, and key points for a podcast discussion.

CONTENT:
${content}

Return the response as JSON in this exact format:
{
  "title": "Brief, engaging title for the podcast episode",
  "summary": "2-3 sentence summary of the main topic",
  "keyPoints": ["Key point 1", "Key point 2", "Key point 3"]
}`;

    try {
      const response = await openai.chat.completions.create({
        model: "gpt-5",
        messages: [
          {
            role: "system",
            content: "You are an expert content analyst. Always respond with valid JSON in the specified format."
          },
          {
            role: "user",
            content: prompt
          }
        ],
        response_format: { type: "json_object" },
        temperature: 1.0,
        max_completion_tokens: 500,
      });

      const result = JSON.parse(response.choices[0].message.content || "{}");
      return result;
    } catch (error) {
      console.error("Error analyzing content:", error);
      throw new Error(`Failed to analyze content: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }
}

server/services/tts.ts.content:
import fs from "fs";
import path from "path";
import { randomUUID } from "crypto";
import type { GeneratedScript, ScriptSegment } from "@shared/schema";

export class TTSService {
  private audioDir: string;

  constructor() {
    this.audioDir = path.join(process.cwd(), "generated_audio");
    if (!fs.existsSync(this.audioDir)) {
      fs.mkdirSync(this.audioDir, { recursive: true });
    }
  }

  async synthesizePodcast(
    script: GeneratedScript,
    settings: {
      maleVoice: string;
      femaleVoice: string;
      maleSpeed: number;
      femaleSpeed: number;
    }
  ): Promise<{ audioUrl: string; duration: number }> {
    try {
      // For demonstration, we'll use ElevenLabs API
      // In production, you'd use the actual ElevenLabs SDK or API
      const apiKey = process.env.ELEVENLABS_API_KEY;

      console.log("ElevenLabs API Key available:", !!apiKey);

      if (!apiKey) {
        throw new Error("ElevenLabs API key not found in environment variables");
      }

      const audioSegments: Buffer[] = [];
      let totalDuration = 0;

      for (const segment of script.segments) {
        const audio = await this.synthesizeSegment(segment, settings, apiKey);
        audioSegments.push(audio.buffer);
        totalDuration += audio.duration;
      }

      // Combine audio segments
      const combinedAudio = Buffer.concat(audioSegments);

      // Save to file
      const fileName = `podcast_${randomUUID()}.mp3`;
      const filePath = path.join(this.audioDir, fileName);
      fs.writeFileSync(filePath, combinedAudio);

      return {
        audioUrl: `/api/audio/${fileName}`,
        duration: totalDuration
      };
    } catch (error) {
      console.error("Error synthesizing podcast:", error);

      // Check if it's a quota error and provide helpful message
      if (error instanceof Error && error.message.includes('quota_exceeded')) {
        console.log("🔄 ElevenLabs quota exceeded, creating fallback audio file...");

        // Create a basic silent audio file when quota is exceeded
        const fallbackBuffer = Buffer.alloc(1024);
        const fileName = `podcast_${randomUUID()}.mp3`;
        const filePath = path.join(this.audioDir, fileName);
        fs.writeFileSync(filePath, fallbackBuffer);

        return {
          audioUrl: `/api/audio/${fileName}`,
          duration: 5 // 5 seconds placeholder
        };
      }

      throw new Error(`Failed to synthesize audio: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  private async synthesizeSegment(
    segment: ScriptSegment,
    settings: {
      maleVoice: string;
      femaleVoice: string;
      maleSpeed: number;
      femaleSpeed: number;
    },
    apiKey: string
  ): Promise<{ buffer: Buffer; duration: number }> {
    // Voice ID mapping - these would be actual ElevenLabs voice IDs
    const voiceIds = {
      male: {
        David: "21m00Tcm4TlvDq8ikWAM",
        James: "2EiwWnXFnvU5JabPnv8n",
        Michael: "flq6f7yk4E4fJM5XTYuZ",
        Ryan: "wViXBPUzp2ZZixB1xQuM"
      },
      female: {
        Sarah: "EXAVITQu4vr4xnSDxMaL",
        Emma: "ThT5KcBeYPX3keUQqHPh",
        Lisa: "XB0fDUnXU5powFXDhCwa",
        Rachel: "pNInz6obpgDQGcFmaJgB"
      }
    };

    const voiceId = segment.speaker === "male" 
      ? voiceIds.male[settings.maleVoice as keyof typeof voiceIds.male] || voiceIds.male.David
      : voiceIds.female[settings.femaleVoice as keyof typeof voiceIds.female] || voiceIds.female.Sarah;

    const speed = segment.speaker === "male" ? settings.maleSpeed : settings.femaleSpeed;

    try {
      console.log(`Making ElevenLabs API call for voice ${voiceId}`);
      console.log(`Request text length: ${segment.content.length} characters`);

      // ElevenLabs API call with simplified voice settings
      const response = await fetch(`https://api.elevenlabs.io/v1/text-to-speech/${voiceId}`, {
        method: "POST",
        headers: {
          "Accept": "audio/mpeg", 
          "Content-Type": "application/json",
          "xi-api-key": apiKey,
        },
        body: JSON.stringify({
          text: segment.content,
          model_id: "eleven_monolingual_v1",
          voice_settings: {
            stability: 0.5,
            similarity_boost: 0.5
          }
        }),
      });

      console.log(`ElevenLabs API response status: ${response.status}`);

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`ElevenLabs API error ${response.status}:`, errorText);

        // Check for quota exceeded error
        if (response.status === 401 && errorText.includes('quota_exceeded')) {
          throw new Error('ElevenLabs API quota exceeded. Please check your account credits or try again later.');
        }

        throw new Error(`ElevenLabs API error: ${response.status} - ${errorText}`);
      }

      const audioBuffer = Buffer.from(await response.arrayBuffer());

      // Estimate duration based on text length and speed
      // Rough estimate: ~150 words per minute at normal speed
      const wordCount = segment.content.split(" ").length;
      const estimatedDuration = (wordCount / 150) * 60 / speed;

      return {
        buffer: audioBuffer,
        duration: estimatedDuration
      };
    } catch (error) {
      // Fallback: create a silent audio buffer as placeholder
      console.error("TTS error:", error);

      // Create a minimal MP3 header for a short silent audio
      const silentBuffer = Buffer.alloc(1024);
      return {
        buffer: silentBuffer,
        duration: 1
      };
    }
  }

  getAudioFilePath(fileName: string): string {
    return path.join(this.audioDir, fileName);
  }
}

client/src/components/AudioPlayer.tsx.content:
import { useState, useRef, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Slider } from "@/components/ui/slider";
import { PlayCircle, PauseCircle, Volume2 } from "lucide-react";

interface AudioPlayerProps {
  podcastId: string;
}

export default function AudioPlayer({ podcastId }: AudioPlayerProps) {
  const audioRef = useRef<HTMLAudioElement>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(75);
  const [playbackRate, setPlaybackRate] = useState(1);

  const { data: status } = useQuery({
    queryKey: ["/api/podcast", podcastId, "status"],
    enabled: !!podcastId,
  });

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    const updateTime = () => setCurrentTime(audio.currentTime);
    const updateDuration = () => setDuration(audio.duration);
    const handleEnded = () => setIsPlaying(false);

    audio.addEventListener('timeupdate', updateTime);
    audio.addEventListener('loadedmetadata', updateDuration);
    audio.addEventListener('ended', handleEnded);

    return () => {
      audio.removeEventListener('timeupdate', updateTime);
      audio.removeEventListener('loadedmetadata', updateDuration);
      audio.removeEventListener('ended', handleEnded);
    };
  }, [(status as any)?.audioUrl]);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume / 100;
    }
  }, [volume]);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.playbackRate = playbackRate;
    }
  }, [playbackRate]);

  if ((status as any)?.status !== "completed" || !(status as any)?.audioUrl) {
    return null;
  }

  const togglePlayback = async () => {
    const audio = audioRef.current;
    if (!audio) return;

    try {
      if (isPlaying) {
        audio.pause();
        setIsPlaying(false);
      } else {
        await audio.play();
        setIsPlaying(true);
      }
    } catch (error) {
      console.error('Audio playback error:', error);
    }
  };

  const handleSeek = (value: number[]) => {
    const audio = audioRef.current;
    if (!audio) return;

    audio.currentTime = (value[0] / 100) * duration;
  };

  const formatTime = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const playbackProgress = duration > 0 ? (currentTime / duration) * 100 : 0;

  const speedOptions = [0.75, 1, 1.25, 1.5];

  return (
    <Card className="fade-in">
      <CardContent className="p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4 flex items-center">
          <PlayCircle className="text-primary mr-2" size={20} />
          Generated Podcast
        </h3>

        <audio ref={audioRef} preload="metadata">
          <source src={(status as any)?.audioUrl} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>

        {/* Waveform Visualization */}
        <div className="bg-muted rounded-lg p-6 mb-4">
          <div className="flex items-center justify-center space-x-1 h-16">
            {/* Animated waveform bars */}
            {[20, 35, 45, 30, 55, 40, 25, 60, 35, 25, 45, 30].map((height, index) => (
              <div
                key={index}
                className={`w-1 rounded-full transition-all duration-300 ${
                  isPlaying ? 'bg-primary animate-pulse' : 'bg-primary/60'
                }`}
                style={{ height: `${height}px` }}
              />
            ))}
          </div>
          <div className="bg-gradient-to-r from-primary to-accent h-0.5 w-full mt-2 rounded-full" />
        </div>

        {/* Player Controls */}
        <div className="flex items-center space-x-4">
          <Button
            onClick={togglePlayback}
            size="lg"
            className="w-12 h-12 rounded-full p-0"
            data-testid="button-play-pause"
          >
            {isPlaying ? (
              <PauseCircle size={24} />
            ) : (
              <PlayCircle size={24} />
            )}
          </Button>

          <div className="flex-1">
            <div className="flex items-center justify-between text-sm text-muted-foreground mb-1">
              <span data-testid="text-current-time">{formatTime(currentTime)}</span>
              <span data-testid="text-total-duration">{formatTime(duration)}</span>
            </div>
            <Slider
              value={[playbackProgress]}
              onValueChange={handleSeek}
              max={100}
              step={0.1}
              className="w-full"
              data-testid="slider-progress"
            />
          </div>

          <div className="flex items-center space-x-2">
            <Volume2 className="text-muted-foreground" size={20} />
            <Slider
              value={[volume]}
              onValueChange={(value) => setVolume(value[0])}
              max={100}
              step={1}
              className="w-20"
              data-testid="slider-volume"
            />
          </div>
        </div>

        {/* Playback Speed */}
        <div className="flex items-center justify-center space-x-2 mt-4">
          <span className="text-sm text-muted-foreground">Speed:</span>
          {speedOptions.map((speed) => (
            <Button
              key={speed}
              variant={playbackRate === speed ? "default" : "outline"}
              size="sm"
              onClick={() => setPlaybackRate(speed)}
              className="px-2 py-1 text-sm"
              data-testid={`button-speed-${speed}`}
            >
              {speed}x
            </Button>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ContentInput.tsx.content:
import { useState } from "react";
import { useDropzone } from "react-dropzone";
import { Card, CardContent } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Upload, FileText, AlertCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface ContentInputProps {
  content: string;
  onContentChange: (content: string) => void;
}

export default function ContentInput({ content, onContentChange }: ContentInputProps) {
  const [activeTab, setActiveTab] = useState<"text" | "upload">("text");
  const [isUploading, setIsUploading] = useState(false);
  const { toast } = useToast();

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: {
      "application/vnd.openxmlformats-officedocument.wordprocessingml.document": [".docx"],
      "text/plain": [".txt"],
    },
    maxSize: 10 * 1024 * 1024, // 10MB
    onDrop: async (acceptedFiles) => {
      if (acceptedFiles.length === 0) return;

      setIsUploading(true);
      const formData = new FormData();
      formData.append("document", acceptedFiles[0]);

      try {
        const response = await fetch("/api/upload-document", {
          method: "POST",
          body: formData,
        });

        if (!response.ok) {
          const error = await response.json();
          throw new Error(error.error || "Upload failed");
        }

        const result = await response.json();
        onContentChange(result.content);
        setActiveTab("text");

        toast({
          title: "Document uploaded successfully",
          description: `Extracted ${result.content.length} characters from your document.`,
        });
      } catch (error) {
        console.error("Upload error:", error);
        toast({
          title: "Upload failed",
          description: error instanceof Error ? error.message : "Failed to process document",
          variant: "destructive",
        });
      } finally {
        setIsUploading(false);
      }
    },
  });

  return (
    <Card>
      <CardContent className="p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4 flex items-center">
          <FileText className="text-primary mr-2" size={20} />
          Input Content
        </h3>

        {/* Tab Navigation */}
        <div className="flex space-x-1 mb-4 bg-muted rounded-lg p-1">
          <button
            onClick={() => setActiveTab("text")}
            className={`flex-1 py-2 px-4 rounded-md font-medium text-sm transition-colors ${
              activeTab === "text"
                ? "bg-primary text-primary-foreground"
                : "text-muted-foreground hover:text-foreground"
            }`}
            data-testid="button-text-tab"
          >
            Paste Text
          </button>
          <button
            onClick={() => setActiveTab("upload")}
            className={`flex-1 py-2 px-4 rounded-md font-medium text-sm transition-colors ${
              activeTab === "upload"
                ? "bg-primary text-primary-foreground"
                : "text-muted-foreground hover:text-foreground"
            }`}
            data-testid="button-upload-tab"
          >
            Upload File
          </button>
        </div>

        {/* Text Input Tab */}
        {activeTab === "text" && (
          <div className="space-y-4">
            <label htmlFor="article-content" className="block text-sm font-medium text-foreground">
              Article Content or Transcript
            </label>
            <Textarea
              id="article-content"
              value={content}
              onChange={(e) => onContentChange(e.target.value)}
              rows={12}
              className="resize-none"
              placeholder="Paste your article content here... Our AI will analyze the text and generate an engaging conversation between two podcast hosts discussing the key points, insights, and implications."
              data-testid="textarea-content"
            />
            <div className="flex justify-between items-center text-sm">
              <span className="text-muted-foreground" data-testid="text-character-count">
                {content.length} characters
              </span>
              <span className="text-muted-foreground">Recommended: 500-5,000 characters</span>
              {content.length < 100 && content.length > 0 && (
                <span className="text-destructive flex items-center">
                  <AlertCircle size={16} className="mr-1" />
                  Too short
                </span>
              )}
            </div>
          </div>
        )}

        {/* File Upload Tab */}
        {activeTab === "upload" && (
          <div className="space-y-4">
            <label className="block text-sm font-medium text-foreground">
              Upload Document
            </label>
            <div
              {...getRootProps()}
              className={`border-2 border-dashed rounded-lg p-8 text-center cursor-pointer transition-all ${
                isDragActive
                  ? "border-primary bg-primary/5 scale-[1.02]"
                  : "border-border hover:border-primary hover:bg-primary/5"
              }`}
              data-testid="dropzone-upload"
            >
              <input {...getInputProps()} />
              <Upload className="mx-auto mb-4 text-muted-foreground" size={48} />
              {isUploading ? (
                <div>
                  <p className="text-foreground font-medium mb-2">Processing document...</p>
                  <div className="w-full bg-muted rounded-full h-2">
                    <div className="bg-primary h-2 rounded-full animate-pulse w-1/2"></div>
                  </div>
                </div>
              ) : (
                <>
                  <p className="text-foreground font-medium mb-2">
                    {isDragActive ? "Drop your document here" : "Drag and drop your document here"}
                  </p>
                  <p className="text-muted-foreground text-sm mb-4">
                    Supports .docx and .txt files up to 10MB
                  </p>
                  <Button variant="outline">
                    Choose File
                  </Button>
                </>
              )}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

